import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductCard from './ProductCard';
import './ProductCard.css';

function HomePage() {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedCategory, setSelectedCategory] = useState('all'); // Added state
  const productsPerPage = 10;

  useEffect(() => {
    axios.get('https://fakestoreapi.com/products')
      .then(response => {
        const sortedProducts = response.data.sort((a, b) => b.rating.rate - a.rating.rate);
        setProducts(sortedProducts);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  useEffect(() => {
    setCurrentPage(1); // Reset current page when category changes
  }, [selectedCategory]);

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (endIndex < products.length) {
      setCurrentPage(currentPage + 1);
    }
  };

  const startIndex = (currentPage - 1) * productsPerPage;
  const endIndex = startIndex + productsPerPage;

  const filteredProducts = selectedCategory === 'all'
    ? products
    : products.filter(product => product.category === selectedCategory);

  const displayedProducts = filteredProducts.slice(startIndex, endIndex);

  return (
    <div>
      <h1>Highest Rated Products</h1>
      <div className="search-bar">
        <label htmlFor="category">Search by Category:</label>
        <select
          id="category"
          value={selectedCategory}
          onChange={event => setSelectedCategory(event.target.value)}
        >
          <option value="all">All</option>
          {/* Map through categories and create option elements */}
        </select>
      </div>
      <div className="product-list">
        {displayedProducts.map(product => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>Page {currentPage}</span>
        <button onClick={handleNextPage} disabled={endIndex >= products.length}>
          Next
        </button>
      </div>
    </div>
  );
}

export default HomePage;
