import React from 'react';

function ProductCard({ product }) {
    const renderStars = rating => {
        const fullStars = Math.floor(rating);
        const decimalPart = rating - fullStars;
        const remainingStars = 5 - fullStars;

        const stars = [];

        for (let i = 0; i < fullStars; i++) {
          stars.push(<span key={i} className="star">&#9733;</span>);
        }

        if (decimalPart > 0) {
          const fractionWidth = `${decimalPart * 80}%`; // Adjust width for appearance
          const partialStarStyle = {
            width: fractionWidth,
            overflow: 'hidden',
            display: 'inline-block'
          };

          stars.push(
            <span key="partial" className="star">
              <span className="partial-star" style={partialStarStyle}>
                &#9733;
              </span>
            </span>
          );
        }

        for (let i = 0; i < remainingStars; i++) {
          stars.push(<span key={`empty-${i}`} className="star">&#9734;</span>);
        }

        return stars;
      };


  return (
    <div className="product-card">
      <img src={product.image} alt={product.title} />
      <h2>{product.title}</h2>
      <p className="price">Price: ${product.price}</p>
      <p className="rating">Rating: {renderStars(product.rating.rate)}</p>
    </div>
  );
}

export default ProductCard;
