import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductCard from './ProductCard';
import './ProductCard.css';

function HomePage() {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchCategory, setSearchCategory] = useState(''); // Added state for search category
  const [displayedProducts, setDisplayedProducts] = useState([]);
  const productsPerPage = 10;

  useEffect(() => {
    axios.get('https://fakestoreapi.com/products')
      .then(response => {
        const sortedProducts = response.data.sort((a, b) => b.rating.rate - a.rating.rate);
        setProducts(sortedProducts);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  useEffect(() => {
    setCurrentPage(1);
  }, [searchCategory]);

  const startIndex = (currentPage - 1) * productsPerPage;
  const endIndex = startIndex + productsPerPage;

  const filteredProductsByCategory = selectedCategory === 'all'
    ? products
    : products.filter(product => product.category === selectedCategory);

  const handleSearch = () => {
    if (searchCategory.trim() !== '') {
      const filteredProducts = filteredProductsByCategory.filter(product =>
        product.category.toLowerCase().includes(searchCategory.toLowerCase())
      );
      setDisplayedProducts(filteredProducts);
    } else {
      setDisplayedProducts([]);
    }
  };

  const displayedProductsSlice = displayedProducts.slice(startIndex, endIndex);

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (endIndex < displayedProducts.length) {
      setCurrentPage(currentPage + 1);
    }
  };

  return (
    <div>
      <h1>Highest Rated Products</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Enter category..."
          value={searchCategory}
          onChange={event => setSearchCategory(event.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div className="product-list">
        {displayedProductsSlice.map(product => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>Page {currentPage}</span>
        <button
          onClick={handleNextPage}
          disabled={endIndex >= displayedProducts.length}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default HomePage;
