import React from 'react';

function MyCart({ cart, onIncrementQuantity, onDecrementQuantity, onRemoveFromCart }) {
  return (
    <div className="cart">
      <h2>My Cart</h2>
      {cart.length === 0 ? <p>Your cart is empty.</p> : (
        <ul>
          {cart.map(cartProduct => (
            <li key={cartProduct.id}>
              <img src={cartProduct.image} alt={cartProduct.title} />
              {cartProduct.title}
              <button className="increment-button" onClick={() => onIncrementQuantity(cartProduct)}>
                +
              </button>
              <span>{cartProduct.quantity}</span>
              <button className="decrement-button" onClick={() => onDecrementQuantity(cartProduct)}>
                -
              </button>
              <button className="remove-button" onClick={() => onRemoveFromCart(cartProduct)}>
                Remove
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default MyCart;
