import React from 'react';

function MyCart({ cart, updateQuantity, removeFromCart }) {
  return (
    <div className="cart">
      <h2>My Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul>
          {cart.map(cartProduct => (
            <li key={cartProduct.id} className="cart-item">
              <div className="cart-item-image">
                <img src={cartProduct.image} alt={cartProduct.title} />
              </div>
              <div className="cart-item-details">
                <p className="cart-item-title">{cartProduct.title}</p>
                <div className="cart-item-quantity">
                <button className="quantity-button" onClick={() => updateQuantity(cartProduct.id, -1)}>-</button>

                </div>
                <button className="remove-button" onClick={() => removeFromCart(cartProduct.id)}>Remove</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default MyCart;
