import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductCard from './ProductCard';

function HomePage() {
  // State to store products and their properties
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchCategory, setSearchCategory] = useState('');
  const [isSearchClicked, setIsSearchClicked] = useState(false);

  // Load cart from local storage on component mount
  const [cart, setCart] = useState(loadCartFromLocalStorage());
  const productsPerPage = 10;

  // State to manage cart display and success popup
  const [myCartPage, setMyCartPage] = useState(false);
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);

  // Load products from API on component mount
  useEffect(() => {
    axios.get('https://fakestoreapi.com/products')
      .then(response => {
        const sortedProducts = response.data.sort((a, b) => b.rating.rate - a.rating.rate);
        setProducts(sortedProducts);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  // Save cart to local storage whenever it changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  // Function to load cart from local storage
  function loadCartFromLocalStorage() {
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  }

  // Calculate the start and end index for displayed products
  const startIndex = (currentPage - 1) * productsPerPage;
  const endIndex = startIndex + productsPerPage;

  // Filter products based on selected category
  const filteredProductsByCategory = selectedCategory === 'all'
    ? products
    : products.filter(product => product.category === selectedCategory);

  // Filter products based on search category
  const filteredProductsBySearchCategory = searchCategory === ''
    ? filteredProductsByCategory
    : filteredProductsByCategory.filter(product =>
        product.category.toLowerCase().includes(searchCategory.toLowerCase())
      );

  // Determine the list of displayed products
  const displayedProducts = isSearchClicked
    ? filteredProductsBySearchCategory.slice(startIndex, endIndex)
    : filteredProductsByCategory.slice(startIndex, endIndex);

  // Function to handle moving to the previous page
  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  // Function to handle moving to the next page
  const handleNextPage = () => {
    if (endIndex < (isSearchClicked ? filteredProductsBySearchCategory : filteredProductsByCategory).length) {
      setCurrentPage(currentPage + 1);
    }
  };

  // Function to handle the search button click
  const handleSearch = () => {
    setIsSearchClicked(true);
    setCurrentPage(1);
  };

  // Function to handle the Home button click
  const handleHome = () => {
    setCurrentPage(1);
    setSelectedCategory('all');
    setSearchCategory('');
    setIsSearchClicked(false);
    setMyCartPage(false); // Reset myCartPage state when going to the home page
  };

  // Function to handle adding a product to the cart
  const handleAddToCart = (product) => {
    // Check if the product is already in the cart
    const existingProduct = cart.find(item => item.product.id === product.id);
    if (existingProduct) {
      // If yes, update the quantity
      const updatedCart = cart.map(item =>
        item.product.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
      setCart(updatedCart);
    } else {
      // If not, add the product with quantity 1
      setCart(prevCart => [...prevCart, { product, quantity: 1 }]);
    }
    setShowSuccessPopup(true);
    setTimeout(() => {
      setShowSuccessPopup(false);
    }, 2000); // Display for 2 seconds
  };

  // Function to handle displaying the cart
  const handleMyCart = () => {
    setMyCartPage(true);
  };

  // Function to handle going back to the product list from cart
  const handleBackToProducts = () => {
    setMyCartPage(false);
  };

  // Function to handle increasing the quantity of a cart item
  const handleIncreaseQuantity = (productId) => {
    const updatedCart = cart.map(item =>
      item.product.id === productId
        ? { ...item, quantity: item.quantity + 1 }
        : item
    );
    setCart(updatedCart);
  };

  // Function to handle decreasing the quantity of a cart item
  const handleDecreaseQuantity = (productId) => {
    const updatedCart = cart.map(item =>
      item.product.id === productId
        ? { ...item, quantity: Math.max(item.quantity - 1, 0) }
        : item
    );
    setCart(updatedCart);
  };

  // Function to handle removing a product from the cart
  const handleRemoveFromCart = (productId) => {
    const updatedCart = cart.filter(item => item.product.id !== productId);
    setCart(updatedCart);
  };

  // Remove items with quantity 0 from cart
  useEffect(() => {
    const updatedCart = cart.filter(item => item.quantity > 0);
    setCart(updatedCart);
  }, [cart]);

  // Calculate the total price of the cart
  const cartTotal = cart.reduce((total, item) => total + item.product.price * item.quantity, 0);

  return (
    <div>
      {/* Header */}
      <header className="header">
        <div className="header-left">
          <button onClick={handleHome} className="home-button">Home</button>
        </div>
        <div className="header-center">
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search by category..."
              value={searchCategory}
              onChange={event => setSearchCategory(event.target.value)}
            />
            <button onClick={handleSearch}>Search</button>
          </div>
        </div>
        <div className="header-right">
          <button className="cart-button" onClick={handleMyCart}>
            My Cart
          </button>
        </div>
        <h1 className="header-title">Highest Rated Products</h1>
      </header>

      {/* Content */}
      {myCartPage ? (
        <div className="cart">
          {/* Cart content */}
        </div>
      ) : (
        <div>
          {/* Product list and pagination */}
        </div>
      )}
    </div>
  );
}

export default HomePage;
