import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductCard from './ProductCard';
import './ProductCard.css';

function HomePage() {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchCategory, setSearchCategory] = useState('');
  const [isSearchClicked, setIsSearchClicked] = useState(false);
  const [cart, setCart] = useState([]); // Cart state to store added products
  const productsPerPage = 10;

  const [myCartPage, setMyCartPage] = useState(false);

  const [showSuccessPopup, setShowSuccessPopup] = useState(false);

  useEffect(() => {
    axios.get('https://fakestoreapi.com/products')
      .then(response => {
        const sortedProducts = response.data.sort((a, b) => b.rating.rate - a.rating.rate);
        setProducts(sortedProducts);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const startIndex = (currentPage - 1) * productsPerPage;
  const endIndex = startIndex + productsPerPage;

  const filteredProductsByCategory = selectedCategory === 'all'
    ? products
    : products.filter(product => product.category === selectedCategory);

  const filteredProductsBySearchCategory = searchCategory === ''
    ? filteredProductsByCategory
    : filteredProductsByCategory.filter(product =>
        product.category.toLowerCase().includes(searchCategory.toLowerCase())
      );

  const displayedProducts = isSearchClicked
    ? filteredProductsBySearchCategory.slice(startIndex, endIndex)
    : filteredProductsByCategory.slice(startIndex, endIndex);

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (endIndex < (isSearchClicked ? filteredProductsBySearchCategory : filteredProductsByCategory).length) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handleSearch = () => {
    setIsSearchClicked(true);
    setCurrentPage(1);
  };

  const handleHome = () => {
    setCurrentPage(1);
    setSelectedCategory('all');
    setSearchCategory('');
    setIsSearchClicked(false);
  };

  const handleAddToCart = (product) => {
    // Check if the product is already in the cart
    const existingProduct = cart.find(item => item.id === product.id);
    if (existingProduct) {
      const updatedCart = cart.map(item =>
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      );
      setCart(updatedCart);
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
    setShowSuccessPopup(true);
    setTimeout(() => {
      setShowSuccessPopup(false);
    }, 2000); // Display for 2 seconds
  };

  const handleMyCart = () => {
    setMyCartPage(true);
  };

  const handleBackToProducts = () => {
    setMyCartPage(false);
  };

  const handleIncreaseQuantity = (productId) => {
    const updatedCart = cart.map(item =>
      item.id === productId ? { ...item, quantity: item.quantity + 1 } : item
    );
    setCart(updatedCart);
  };

  const handleDecreaseQuantity = (productId) => {
    const updatedCart = cart.map(item =>
      item.id === productId ? { ...item, quantity: Math.max(item.quantity - 1, 0) } : item
    );
    setCart(updatedCart);
  };

  const handleRemoveProduct = (productId) => {
    const updatedCart = cart.filter(item => item.id !== productId);
    setCart(updatedCart);
  };

  const calculateTotalPrice = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  return (
    <div>
      <header className="header">
        {/* Header content */}
      </header>

      {myCartPage ? (
        <div className="cart">
          <h2>My Cart</h2>
          {cart.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <div>
              <ul>
                {cart.map(cartProduct => (
                  <li key={cartProduct.id}>
                    <div className="cart-product-info">
                      <img className="cart-product-image" src={cartProduct.image} alt={cartProduct.title} />
                      <div className="cart-product-details">
                        <p className="cart-product-title">{cartProduct.title}</p>
                        <p className="cart-product-price">Price: ${cartProduct.price}</p>
                        <div className="quantity-controls">
                          <button className="quantity-button" onClick={() => handleDecreaseQuantity(cartProduct.id)}>-</button>
                          <span className="cart-product-quantity">{cartProduct.quantity}</span>
                          <button className="quantity-button" onClick={() => handleIncreaseQuantity(cartProduct.id)}>+</button>
                          <button className="remove-button" onClick={() => handleRemoveProduct(cartProduct.id)}>Remove</button>
                        </div>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
              <p className="total-price">Total Price: ${calculateTotalPrice().toFixed(2)}</p>
              <button className="back-to-products-button" onClick={handleBackToProducts}>Back to Products</button>
            </div>
          )}
        </div>
      ) : (
        <div>
          {/* Product list and pagination */}
        </div>
      )}
    </div>
  );
}

export default HomePage;
